mapper.select:
SELECT s.shop_id, a.area_id, a.area_name, sc.shop_category_id, sc.shop_category_name, s.shop_name, s.shop_desc, s.shop_addr, s.phone, s.shop_img, s.longitude, s.latitude, s.priority, s.create_time, s.last_edit_time, s.enable_status, s.advice FROM tb_shop s, tb_area a, tb_shop_category sc
s.area_id = a.area_id and s.shop_category_id = sc.shop_category_id
and s.shop_id = #{shopCondition.shopId}
and s.shop_category_id = #{shopCondition.shopCategory.shopCategoryId}
and s.parent_category_id = #{shopCondition.parentCategory.shopCategoryId}
and s.area_id = #{shopCondition.area.areaId}
and s.shop_name like '%${shopCondition.shopName}%'
and s.enable_status = #{shopCondition.enableStatus}
and s.owner_id = #{shopCondition.owner.userId}
ORDER BY priority DESC LIMIT #{rowIndex},#{pageSize}

SELECT
count(1)
FROM
tb_shop s,
tb_area a,
tb_shop_category sc

	s.area_id = a.area_id
	and
	s.shop_category_id = sc.shop_category_id

		and s.shop_id = #{shopCondition.shopId}


		and s.shop_category_id =
		#{shopCondition.shopCategory.shopCategoryId}


		and s.parent_category_id =
		#{shopCondition.parentCategory.shopCategoryId}


		and s.area_id =
		#{shopCondition.area.areaId}



		and s.shop_name like '%${shopCondition.shopName}%'


		and s.enable_status = #{shopCondition.enableStatus}


		and s.owner_id = #{shopCondition.owner.userId}

mapper.update:
update tb_shop
shop_name=#{shopName}, shop_desc=#{shopDesc}, shop_addr=#{shopAddr}, phone=#{phone}, shop_img=#{shopImg}, longitude=#{longitude}, latitude=#{latitude}, priority=#{priority}, last_edit_time=#{lastEditTime}, enable_status=#{enableStatus}, advice=#{advice}, area_id=#{area.areaId}, shop_category_id=#{shopCategory.shopCategoryId},
where shop_id=#{shopId}
